  import java.util.Map;

import java.util.HashMap;

import sailpoint.object.Identity;

import sailpoint.object.Filter;

Map returnMap = new HashMap();



String struserId = account.getStringAttribute("userid");

Filter filter = Filter.eq("id", strUserId);

Identity identity = context.getUniqueObject(Identity.class, filter);

if(identity == null)

{     

System.out.println("identity cant be fetched based on userid . if authoratative source identity will be created ");

}

returnMap.put("identity", identity);

return returnMap;
