import java.util.UUID; 
import sailpoint.object.Identity;



/**
     * Generate a GUID return The generated GUID
     */
    public String generateGUID() {
      //randomly generates a UUID  
        UUID uuid = UUID.randomUUID();  
      //converts the randomly generated UUID into String  
        String guid = uuid.toString();  
        System.out.println("Generated guid is: " + guid.toString());
        return guid;
    }

        String uniqueId= generateGUID();
        identity.setAttribute("globalID",uniqueId);
        //identity.setName(uniqueId);
