while(foundNoUser){
  if(firstName!=null){
    if(lastName.length()>=12){
      for(int i=0;i<12;i++){
        if(i<=firstName.length()) userName=lastName+firstName.substring(0,i);
        if(userName.length()>12) userName=lastName.substring(0,(12-i))+firstName.substring(0,i);   
        if(i>firstName.length()) userName=userName.substring(0,(12-i))+firstName;
        if(!isUniqueADName(application,"sAMAccountName",userName)) {
          foundNoUser=false;
          break;
          break;
        }
        else j++;
      }
    }
    else{
      for(int i=0;i<12;i++){
        if(i<=firstName.length()) userName=lastName+firstName.substring(0,i);
