import java.util.*;
import sailpoint.object.*;
import sailpoint.connector.*;

System.out.println("Build Map Rule Called");
   HashMap map = DelimitedFileConnector.defaultBuildMap(cols, record);
  if(schema.getObjectType().compareTo(Connector.TYPE_ACCOUNT) == 0){

    String name = map.get("Name");
    String[] nameArray = name.split(" ");
    String firstName = nameArray[0];
    String lastName = nameArray[1];
    String email = firstName+"."+lastName+"@sailpointdemo.com";

    map.put("FirstName",firstName);
    map.put("LastName",lastName);
    map.put("EmailAddress",email);
  }

return map;
