import sailpoint.object.*;


System.out.println("object in xml:"+object.toXml());

String dept=object.getAttribute("Department");

if(dept!=null && dept.equalsIgnoreCase("Information Technology"))
{
object.put("Salary","5000");
}
if(dept!=null && dept.equalsIgnoreCase("Engineering"))
{
object.put("Salary","7000");
}
return object;
