 import sailpoint.object.Identity;
 import sailpoint.object.*;
 import sailpoint.api.*;










Identity idObj =  context.getObjectByName(Identity.class,"James Smith");

List links =  idObj.getLinks();

 for(Link link:links)
 {
   if(link!=null && link.getApplicationName().equals("Active- Directory")){
     return link.getAttribute("distinguishedName");
   }
 }
