  import sailpoint.api.*;
  import sailpoint.object.*;


  Identity id = context.getObjectByName(Identity.class,"Arthur Bryant");
  String firstName=id.getFirstname();

  String lastName=id.getLastname();

  String userName="";
  boolean foundNoUser=true;
  int j=1;

  System.out.println("Entering into  SAMA-Account-NAme Generation");

  public boolean checkUsername(String name){
    Filter f = Filter.and(Filter.eq("links.linkAttr",name),Filter.and(Filter.eq("links.application.name","Active- Directory")));
    QueryOptions qo = new QueryOptions();
    qo.add(f);
    List ids = context.getObjects(Identity.class,qo);
    if(ids.size()>0) return true;
    else return false;
  }

  //#########################  LAST+FIRST  #########################

  while(foundNoUser){


  //#########################  LAST+FIRST  #########################

  if(firstName!=null){
    if(lastName.length()>=12){
      for(int i=0;i<12;i++){
        if(i<=firstName.length()) userName=lastName+firstName.substring(0,i);
        if(userName.length()>12) userName=lastName.substring(0,(12-i))+firstName.substring(0,i);   
        if(i>firstName.length()) userName=userName.substring(0,(12-i))+firstName;
        if(!checkUsername(userName)) foundNoUser=false;
            else j++;
      }
    }
    else{
      for(int i=0;i<12;i++){
        if(i<=firstName.length()) userName=lastName+firstName.substring(0,i);
        else if(firstName.length()!=lastName.length()) break;
          if(userName.length()>12) userName=lastName.substring(0,(12-i))+firstName.substring(0,i);   
          if(i>firstName.length()) userName=userName.substring(0,(12-i))+firstName;

         if(!checkUsername(userName)) foundNoUser=false;
            else j++;

      }
    }
  }

  //#########################  FIRST+LAST  #########################
  if(lastName!=null){
    if(firstName.length()>=12){
      for(int i=0;i<12;i++){
        if(i<=lastName.length()) userName=firstName+lastName.substring(0,i);
        if(userName.length()>12) userName=firstName.substring(0,(12-i))+lastName.substring(0,i); 
        if(i>lastName.length()) userName=userName.substring(0,(12-i))+lastName;
        if(!checkUsername(userName)) foundNoUser=false;
            else j++;
      }
    }

    else{
      if(lastName.length()>firstName.length()){
        for(int i=0;i<12;i++){
          if(i<lastName.length()) userName=firstName+lastName.substring(0,i);
					if(userName.length()>12) userName=firstName.substring(0,(12-i))+lastName.substring(0,i);  //new
          if(!checkUsername(userName)) foundNoUser=false;
            else j++;
        }
      }


      else if(lastName.length()==firstName.length()){
        for(int i=0;i<12;i++){
          if(i<=lastName.length()) userName=firstName+lastName.substring(0,i);
          if(userName.length()>12) userName=firstName.substring(0,(12-i))+lastName.substring(0,i); 
          if(i>lastName.length()) userName=userName.substring(0,(12-i))+lastName;
          if(!checkUsername(userName)) foundNoUser=false;
            else j++;
        }
      }


      else{
        for(int i=0;i<12;i++){
          if(i<lastName.length()) userName=firstName.substring(0,(firstName.length()-i))+lastName.substring(0,i);
          else {
            if(i<=firstName.length()) userName=firstName.substring(0,(firstName.length()-i+1))+lastName;
            else break;
          }
          if(userName.length()>12) userName=firstName.substring(0,(12-i))+lastName.substring(0,i);

          if(i>lastName.length()) userName=userName.substring(0,(12-i))+lastName;
          if(!checkUsername(userName)) foundNoUser=false;
            else j++;
        }
      }
    }

  }


